mapper.select:
SELECT DISTINCT YTT.uId ,YTT.name,YTT.accountName,YTT.jobNum,YTT.departName,YTT.departId,YTT.accountId ,YTT.positionName,YTT.classTimeName,YTT.classTimeType,YTT.shiftDate,YTT.startTime,YTT.endTime,YTT.foreAttTime AS firstTime,YTT.afterAttTime AS lastTime ,(CASE WHEN YTT.classTimeType=1 AND YTT.foreAttTime <= YTT.startTime THEN '正常' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.startTime, YTT.foreAttTime)<=30 THEN '迟到' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.startTime, YTT.foreAttTime) BETWEEN 30 AND 120 THEN '旷工' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.startTime, YTT.foreAttTime) > 120 THEN '打卡异常' WHEN YTT.classTimeType=1 AND YTT.foreAttTime IS NULL THEN '未打卡' WHEN YTT.classTimeType=2 THEN YTT.classTimeName END)AS firstTimeState ,(CASE WHEN YTT.classTimeType=1 AND YTT.afterAttTime>YTT.endTime THEN '正常' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.afterAttTime, YTT.endTime)<=30 THEN '早退' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.afterAttTime, YTT.endTime) BETWEEN 30 AND 120 THEN '旷工' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.afterAttTime, YTT.endTime) > 120 THEN '打卡异常' END)AS lastTimeState FROM (SELECT YMD_TDP.YYYYMMDD,YMD_TDP.uId,YMD_TDP.name,YMD_TDP.accountName,YMD_TDP.jobNum,YMD_TDP.departName,YMD_TDP.departId,YMD_TDP.accountId,YMD_TDP.positionName ,TAPC.classTimeName,TAPC.classTimeType,TAPC.shiftDate,TAPC.startTime,TAPC.endTime ,max(TAA.attTime) AS afterAttTime ,min(TAA.attTime) AS foreAttTime FROM ( SELECT YMD.YYYYMMDD,TDP.uId,TDP.name,TDP.accountName,TDP.jobNum,TDP.departName,TDP.departId,TDP.accountId,TDP.positionName FROM (SELECT CONVERT(CHAR(10), DATEADD(DD,NUMBER,#{beginDate}),120) AS YYYYMMDD FROM MASTER..SPT_VALUES WHERE TYPE = 'P' AND DATEDIFF(MI,DATEADD(DD,NUMBER, #{beginDate}),#{endDate})>=0 )YMD , (SELECT DISTINCT a.ID AS uId,a.Name AS name,c.Account_Name AS accountName, a.JobNum AS jobNum,b.Name AS departName,b.ID AS departId,a.Account AS accountId, d.Position_Name AS positionName FROM Account_Data c LEFT JOIN T_Employee_Basic a ON a.Account=c.Account_ID LEFT JOIN T_Department b ON a.Department_ID=b.ID LEFT JOIN Position_Data d ON c.Position_ID=d.Position_ID
AND a.Name=#{name}
AND b.Name=#{departName}
AND (b.ID=#{departId} OR b.ID IN ( SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId} ) OR b.ID IN ( SELECT ID FROM T_Department TD WHERE TD.ParentID IN( SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId} ) ) )
AND c.Account_Name=#{accountName}
AND c.Account_ID=#{accountId}
AND d.Position_Name=#{positionName}
AND a.JobNum=#{jobNum}
AND (a.ResignDate>#{beginDate} OR a.ResignDate IS NULL or a.ResignDate='1900-01-01 00:00:00.000')
AND a.entryDate<#{endDate}
AND a.JobNum != '0' AND a.IsDelete='0'
)TDP )YMD_TDP LEFT JOIN (SELECT e.UserID AS accountId,f.Name AS classTimeName,f.Type AS classTimeType,f.ShiftDate AS shiftDate,f.StartTime AS startTime,f.EndTime AS endTime FROM T_Att_PeopleShift e LEFT JOIN T_Att_Shift f ON e.ShiftID=f.ID
AND f.ShiftDate>=#{beginDate}
AND f.ShiftDate <= #{endDate}
)TAPC ON YMD_TDP.accountId = TAPC.accountId AND YMD_TDP.YYYYMMDD = CONVERT(varchar(100), TAPC.shiftDate, 23) LEFT JOIN (SELECT a.AttTime AS attTime ,a.Userid AS accountId FROM T_Att_Attendance a INNER JOIN T_AttLog b ON a.AttLogID=b.ID )TAA ON TAA.accountId = YMD_TDP.accountId AND YMD_TDP.YYYYMMDD = CONVERT(varchar(100), TAA.attTime, 23) GROUP BY YMD_TDP.YYYYMMDD,YMD_TDP.uId,YMD_TDP.name,YMD_TDP.accountName,YMD_TDP.jobNum,YMD_TDP.departName,YMD_TDP.departId ,YMD_TDP.accountId,YMD_TDP.positionName,TAPC.classTimeName,TAPC.classTimeType,TAPC.shiftDate,TAPC.startTime,TAPC.endTime )YTT ORDER BY YTT.uId,firstTimeState,YTT.shiftDate

SELECT YTTD.departName AS departKQName ,YTTD.departId AS departKQId ,COUNT(CASE WHEN YTTD.firstTimeState='迟到' THEN 1 END) AS 'laterTimes' ,COUNT(CASE WHEN YTTD.lastTimeState='早退' THEN 2 END) AS 'leaveEarlyTimes' ,COUNT(CASE WHEN YTTD.firstTimeState='旷工' OR YTTD.lastTimeState='旷工' THEN 3 END) AS 'minersTimes' ,COUNT(CASE WHEN YTTD.firstTimeState='打卡异常' THEN 4 END) AS 'onPA' ,COUNT(CASE WHEN YTTD.lastTimeState='打卡异常' THEN 5 END) AS 'downPA' ,COUNT(CASE WHEN YTTD.firstTimeState='休息天数' THEN 6 END) AS 'restDays' ,COUNT(CASE WHEN YTTD.firstTimeState='加班' THEN 7 END) AS 'overTimesDays' FROM (SELECT YTT.YYYYMMDD,YTT.uId ,YTT.name,YTT.accountName,YTT.jobNum,YTT.departName,YTT.departId,YTT.accountId ,YTT.positionName,YTT.classTimeName,YTT.classTimeType,YTT.shiftDate,YTT.startTime,YTT.endTime,YTT.foreAttTime AS firstTime,YTT.afterAttTime AS lastTime ,(CASE WHEN YTT.classTimeType=1 AND YTT.foreAttTime <= YTT.startTime THEN '正常' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.startTime, YTT.foreAttTime)<=30 THEN '迟到' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.startTime, YTT.foreAttTime) BETWEEN 30 AND 120 THEN '旷工' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.startTime, YTT.foreAttTime) > 120 THEN '打卡异常' WHEN YTT.classTimeType=1 AND YTT.foreAttTime IS NULL THEN '未打卡' WHEN YTT.classTimeType=2 AND DATEDIFF(HOUR, YTT.afterAttTime, YTT.foreAttTime)<= 8 THEN YTT.classTimeName WHEN YTT.classTimeType=2 AND DATEDIFF(HOUR, YTT.afterAttTime, YTT.foreAttTime)> 8 THEN '加班' WHEN YTT.classTimeType=2 THEN YTT.classTimeName END)AS firstTimeState ,(CASE WHEN YTT.classTimeType=1 AND YTT.afterAttTime>YTT.endTime THEN '正常' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.afterAttTime, YTT.endTime)<=30 THEN '早退' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.afterAttTime, YTT.endTime) BETWEEN 30 AND 120 THEN '旷工' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.afterAttTime, YTT.endTime) > 120 THEN '打卡异常' END)AS lastTimeState FROM (SELECT YMD_TDP.YYYYMMDD,YMD_TDP.uId,YMD_TDP.name,YMD_TDP.accountName,YMD_TDP.jobNum,YMD_TDP.departName,YMD_TDP.departId,YMD_TDP.accountId,YMD_TDP.positionName ,TAPC.classTimeName,TAPC.classTimeType,TAPC.shiftDate,TAPC.startTime,TAPC.endTime ,max(TAA.attTime) AS afterAttTime ,min(TAA.attTime) AS foreAttTime FROM ( SELECT YMD.YYYYMMDD,TDP.uId,TDP.name,TDP.accountName,TDP.jobNum,TDP.departName,TDP.departId,TDP.accountId,TDP.positionName FROM (SELECT CONVERT(CHAR(10), DATEADD(DD,NUMBER,#{beginDate}),120) AS YYYYMMDD FROM MASTER..SPT_VALUES WHERE TYPE = 'P' AND DATEDIFF(MI,DATEADD(DD,NUMBER, #{beginDate}),#{endDate})>=0 )YMD , (SELECT DISTINCT a.ID AS uId,a.Name AS name,c.Account_Name AS accountName, a.JobNum AS jobNum,b.Name AS departName,b.ID AS departId,a.Account AS accountId, d.Position_Name AS positionName FROM Account_Data c LEFT JOIN T_Employee_Basic a ON a.Account=c.Account_ID LEFT JOIN T_Department b ON a.Department_ID=b.ID LEFT JOIN Position_Data d ON c.Position_ID=d.Position_ID
AND a.Name=#{name}
AND b.Name=#{departName}
AND (b.ID=#{departId} OR b.ID IN ( SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId} ) OR b.ID IN ( SELECT ID FROM T_Department TD WHERE TD.ParentID IN( SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId} ) ) )
AND c.Account_Name=#{accountName}
AND d.Position_Name=#{positionName}
AND a.JobNum=#{jobNum}
AND (a.ResignDate>#{beginDate} OR a.ResignDate IS NULL or a.ResignDate='1900-01-01 00:00:00.000')
AND a.entryDate<#{endDate}
AND a.JobNum != '0' AND a.IsDelete='0'
)TDP )YMD_TDP LEFT JOIN (SELECT e.UserID AS accountId,f.Name AS classTimeName,f.Type AS classTimeType,f.ShiftDate AS shiftDate,f.StartTime AS startTime,f.EndTime AS endTime FROM T_Att_PeopleShift e LEFT JOIN T_Att_Shift f ON e.ShiftID=f.ID
AND f.ShiftDate>=#{beginDate}
AND f.ShiftDate<=#{endDate}
)TAPC ON YMD_TDP.accountId = TAPC.accountId AND YMD_TDP.YYYYMMDD = CONVERT(varchar(100), TAPC.shiftDate, 23) LEFT JOIN (SELECT a.AttTime AS attTime ,a.Userid AS accountId FROM T_Att_Attendance a INNER JOIN T_AttLog b ON a.AttLogID=b.ID )TAA ON TAA.accountId = YMD_TDP.accountId AND YMD_TDP.YYYYMMDD = CONVERT(varchar(100), TAA.attTime, 23) GROUP BY YMD_TDP.YYYYMMDD,YMD_TDP.uId,YMD_TDP.name,YMD_TDP.accountName,YMD_TDP.jobNum,YMD_TDP.departName,YMD_TDP.departId ,YMD_TDP.accountId,YMD_TDP.positionName,TAPC.classTimeName,TAPC.classTimeType,TAPC.shiftDate,TAPC.startTime,TAPC.endTime )YTT
)YTTD GROUP BY YTTD.departName,YTTD.departId

SELECT YTTG.YYYYMMDD,YTTG.uId ,YTTG.name,YTTG.accountName,YTTG.jobNum,YTTG.departName,YTTG.departId,YTTG.accountId ,YTTG.positionName,YTTG.classTimeName,YTTG.classTimeType,YTTG.shiftDate,YTTG.startTime,YTTG.endTime,YTTG.firstTime,YTTG.lastTime ,YTTG.firstTimeState ,YTTG.lastTimeState FROM (SELECT YTT.YYYYMMDD,YTT.uId ,YTT.name,YTT.accountName,YTT.jobNum,YTT.departName,YTT.departId,YTT.accountId ,YTT.positionName,YTT.classTimeName,YTT.classTimeType,YTT.shiftDate,YTT.startTime,YTT.endTime,YTT.foreAttTime AS firstTime,YTT.afterAttTime AS lastTime ,(CASE WHEN YTT.classTimeType=1 AND YTT.foreAttTime <= YTT.startTime THEN '正常' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.startTime, YTT.foreAttTime)<=30 THEN '迟到' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.startTime, YTT.foreAttTime) BETWEEN 30 AND 120 THEN '旷工' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.startTime, YTT.foreAttTime) > 120 THEN '打卡异常' WHEN YTT.classTimeType=1 AND YTT.foreAttTime IS NULL THEN '未打卡' WHEN YTT.classTimeType=2 THEN YTT.classTimeName END)AS firstTimeState ,(CASE WHEN YTT.classTimeType=1 AND YTT.afterAttTime>YTT.endTime THEN '正常' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.afterAttTime, YTT.endTime) <=30 THEN '早退' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.afterAttTime, YTT.endTime) BETWEEN 30 AND 120 THEN '旷工' WHEN YTT.classTimeType=1 AND DATEDIFF(minute, YTT.afterAttTime, YTT.endTime) > 120 THEN '打卡异常' END)AS lastTimeState FROM (SELECT YMD_TDP.YYYYMMDD,YMD_TDP.uId,YMD_TDP.name,YMD_TDP.accountName,YMD_TDP.jobNum,YMD_TDP.departName,YMD_TDP.departId,YMD_TDP.accountId,YMD_TDP.positionName ,TAPC.classTimeName,TAPC.classTimeType,TAPC.shiftDate,TAPC.startTime,TAPC.endTime ,max(TAA.attTime) AS afterAttTime ,min(TAA.attTime) AS foreAttTime FROM ( SELECT YMD.YYYYMMDD,TDP.uId,TDP.name,TDP.accountName,TDP.jobNum,TDP.departName,TDP.departId,TDP.accountId,TDP.positionName FROM ( SELECT CONVERT(CHAR(10), DATEADD(DD,NUMBER,#{beginDate}),120) AS YYYYMMDD FROM MASTER..SPT_VALUES WHERE TYPE = 'P' AND DATEDIFF(MI,DATEADD(DD,NUMBER, #{beginDate}),#{endDate})>=0 )YMD , (SELECT DISTINCT a.ID AS uId,a.Name AS name,c.Account_Name AS accountName, a.JobNum AS jobNum,b.Name AS departName,b.ID AS departId,a.Account AS accountId, d.Position_Name AS positionName FROM Account_Data c LEFT JOIN T_Employee_Basic a ON a.Account=c.Account_ID LEFT JOIN T_Department b ON a.Department_ID=b.ID LEFT JOIN Position_Data d ON c.Position_ID=d.Position_ID
AND (b.ID=#{departId} OR b.ID IN ( SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId} ) OR b.ID IN ( SELECT ID FROM T_Department TD WHERE TD.ParentID IN( SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId} ) ) )
AND (a.ResignDate>#{beginDate} OR a.ResignDate IS NULL or a.ResignDate='1900-01-01 00:00:00.000')
AND a.entryDate<#{endDate}
AND a.JobNum != '0' AND a.IsDelete='0'
)TDP )YMD_TDP LEFT JOIN (SELECT e.UserID AS accountId,f.Name AS classTimeName,f.Type AS classTimeType,f.ShiftDate AS shiftDate,f.StartTime AS startTime,f.EndTime AS endTime FROM T_Att_PeopleShift e LEFT JOIN T_Att_Shift f ON e.ShiftID=f.ID
AND f.ShiftDate>=#{beginDate}
AND f.ShiftDate<=#{endDate}
)TAPC ON YMD_TDP.accountId = TAPC.accountId AND YMD_TDP.YYYYMMDD = CONVERT(varchar(100), TAPC.shiftDate, 23) LEFT JOIN (SELECT a.AttTime AS attTime ,a.Userid AS accountId FROM T_Att_Attendance a INNER JOIN T_AttLog b ON a.AttLogID=b.ID )TAA ON TAA.accountId = YMD_TDP.accountId AND YMD_TDP.YYYYMMDD = CONVERT(varchar(100), TAA.attTime, 23) GROUP BY YMD_TDP.YYYYMMDD,YMD_TDP.uId,YMD_TDP.name,YMD_TDP.accountName,YMD_TDP.jobNum,YMD_TDP.departName,YMD_TDP.departId ,YMD_TDP.accountId,YMD_TDP.positionName,TAPC.classTimeName,TAPC.classTimeType,TAPC.shiftDate,TAPC.startTime,TAPC.endTime )YTT )YTTG WHERE (firstTimeState='迟到' OR firstTimeState='旷工' OR firstTimeState='打卡异常') OR (lastTimeState='迟到' OR lastTimeState='旷工' OR lastTimeState='打卡异常') ORDER BY YTTG.uId

