20 THEN '打卡异常' END)AS lastTimeState FROM (SELECT YMD_TDP.YYYYMMDD,YMD_TDP.uId,YMD_TDP.name,YMD_TDP.accountName,YMD_TDP.jobNum,YMD_TDP.departName,YMD_TDP.departId,YMD_TDP.accountId,YMD_TDP.positionName ,TAPC.classTimeName,TAPC.classTimeType,TAPC.shiftDate,TAPC.startTime,TAPC.endTime ,max(TAA.attTime) AS afterAttTime ,min(TAA.attTime) AS foreAttTime FROM ( SELECT YMD.YYYYMMDD,TDP.uId,TDP.name,TDP.accountName,TDP.jobNum,TDP.departName,TDP.departId,TDP.accountId,TDP.positionName FROM (SELECT CONVERT(CHAR(10), DATEADD(DD,NUMBER,#{beginDate}),120) AS YYYYMMDD FROM MASTER..SPT_VALUES WHERE TYPE = 'P' AND DATEDIFF(MI,DATEADD(DD,NUMBER, #{beginDate}),#{endDate})>=0 )YMD , (SELECT DISTINCT a.ID AS uId,a.Name AS name,c.Account_Name AS accountName, a.JobNum AS jobNum,b.Name AS departName,b.ID AS departId,a.Account AS accountId, d.Position_Name AS positionName FROM Account_Data c LEFT JOIN T_Employee_Basic a ON a.Account=c.Account_ID LEFT JOIN T_Department b ON a.Department_ID=b.ID LEFT JOIN Position_Data d ON c.Position_ID=d.Position_ID
AND a.Name=#{name}
AND b.Name=#{departName}
AND (b.ID=#{departId} OR b.ID IN ( SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId} ) OR b.ID IN ( SELECT ID FROM T_Department TD WHERE TD.ParentID IN( SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId} ) ) )
AND c.Account_Name=#{accountName}
AND d.Position_Name=#{positionName}
AND a.JobNum=#{jobNum}
AND (a.ResignDate>#{beginDate} OR a.ResignDate IS NULL or a.ResignDate='1900-01-01 00:00:00.000')
AND a.entryDate<#{endDate}
AND a.JobNum != '0' AND a.IsDelete='0'
)TDP )YMD_TDP LEFT JOIN (SELECT e.UserID AS accountId,f.Name AS classTimeName,f.Type AS classTimeType,f.ShiftDate AS shiftDate,f.StartTime AS startTime,f.EndTime AS endTime FROM T_Att_PeopleShift e LEFT JOIN T_Att_Shift f ON e.ShiftID=f.ID
