)YTTD GROUP BY YTTD.uId,YTTD.name,YTTD.jobNum,YTTD.accountId ,YTTD.departName,YTTD.departId,YTTD.positionName

SELECT YTTD.uId AS ryKQId, YTTD.name AS ryKQName, YTTD.jobNum AS ryJobNum, YTTD.departName AS ryDepartKQName, YTTD.departId AS ryDepartKQId , YTTD.positionName AS ryPositionKQName,YTTD.entryDate AS ryEnteryTime,YTTD.ResignDate AS ryLeaveTime , COUNT(CASE WHEN YTTD.firstTimeState = '正常' THEN 1 END) AS ryOnNomalPA , COUNT(CASE WHEN YTTD.lastTimeState = '正常' THEN 2 END) AS ryDownNomalPA , COUNT(CASE WHEN YTTD.firstTimeState = '迟到' THEN 3 END) AS ryLaterTimes , COUNT(CASE WHEN YTTD.lastTimeState = '早退' THEN 4 END) AS ryLeaveEarlyTimes , COUNT(CASE WHEN YTTD.firstTimeState = '旷工' OR YTTD.lastTimeState = '旷工' THEN 5 END) AS ryMinersTimes , COUNT(CASE WHEN YTTD.firstTimeState = '打卡异常' OR YTTD.lastTimeState = '打卡异常' THEN 6 END) AS ryOnPA , COUNT(CASE WHEN YTTD.lastTimeState = '打卡异常' THEN 7 END) AS ryDownPA , COUNT(CASE WHEN YTTD.firstTimeState = '未打卡' THEN 8 END) AS ryNotPA , COUNT(CASE WHEN YTTD.classTimeType = 2 THEN 9 END) AS ryRestDays , COUNT(CASE WHEN YTTD.firstTimeState = '加班' THEN 10 END) AS ryOverTimesDays , COUNT(CASE WHEN YTTD.firstTimeState = '未排班' THEN 11 END) AS ryNoScheduleTimes , (CASE WHEN COUNT(CASE WHEN YTTD.firstTimeState = '未入职' THEN 12 END)>0 THEN '入职' END) AS notEntry , (CASE WHEN COUNT(CASE WHEN YTTD.firstTimeState = '已离职' THEN 13 END)>0 THEN '离职' END) AS leavedPosition FROM ( SELECT YTT.YYYYMMDD, YTT.uId, YTT.name, YTT.accountName, YTT.jobNum,YTT.entryDate,YTT.ResignDate , YTT.departName, YTT.departId, YTT.accountId, YTT.positionName, YTT.classTimeName , YTT.classTimeType, YTT.shiftDate, YTT.startTime, YTT.endTime, YTT.foreAttTime AS firstTime , YTT.afterAttTime AS lastTime , CASE WHEN YTT.classTimeType = 1 AND YTT.foreAttTime <= YTT.startTime THEN '正常' WHEN YTT.classTimeType = 1 AND DATEDIFF(minute, YTT.startTime, YTT.foreAttTime) <= 30 THEN '迟到' WHEN YTT.classTimeType = 1 AND DATEDIFF(minute, YTT.startTime, YTT.foreAttTime) BETWEEN 30 AND 120 THEN '旷工' WHEN YTT.classTimeType = 1 AND DATEDIFF(minute, YTT.startTime, YTT.foreAttTime) > 120 THEN '打卡异常' WHEN YTT.classTimeType = 1 AND YTT.foreAttTime IS NULL THEN '未打卡' WHEN YTT.classTimeType = 2 THEN YTT.classTimeName WHEN YTT.classTimeType IS NULL AND YTT.YYYYMMDD>=YTT.entryDate AND YTT.YYYYMMDD<=YTT.ResignDate THEN '未排班' WHEN YTT.classTimeType IS NULL AND YTT.YYYYMMDD < YTT.entryDate THEN '未入职' WHEN YTT.classTimeType IS NULL AND YTT.YYYYMMDD>YTT.ResignDate THEN '已离职' END AS firstTimeState , CASE WHEN YTT.classTimeType = 1 AND YTT.afterAttTime > YTT.endTime THEN '正常' WHEN YTT.classTimeType = 1 AND DATEDIFF(minute, YTT.afterAttTime, YTT.endTime) <= 30 THEN '迟到' WHEN YTT.classTimeType = 1 AND DATEDIFF(minute, YTT.afterAttTime, YTT.endTime) BETWEEN 30 AND 120 THEN '旷工' WHEN YTT.classTimeType = 1 AND DATEDIFF(minute, YTT.afterAttTime, YTT.endTime) > 120 THEN '打卡异常' END AS lastTimeState FROM ( SELECT YMD_TDP.YYYYMMDD, YMD_TDP.uId, YMD_TDP.name, YMD_TDP.accountName, YMD_TDP.jobNum , YMD_TDP.departName, YMD_TDP.departId, YMD_TDP.accountId, YMD_TDP.positionName, TAPC.classTimeName , TAPC.classTimeType, TAPC.shiftDate, TAPC.startTime, TAPC.endTime,YMD_TDP.entryDate,YMD_TDP.ResignDate , MAX(TAA.attTime) AS afterAttTime, MIN(TAA.attTime) AS foreAttTime FROM ( SELECT YMD.YYYYMMDD, TDP.uId, TDP.name, TDP.accountName, TDP.jobNum , TDP.departName, TDP.departId, TDP.accountId, TDP.positionName,TDP.entryDate,TDP.ResignDate FROM ( SELECT CONVERT(CHAR(10), DATEADD(DD,NUMBER,#{beginDate}),120) AS YYYYMMDD FROM MASTER..SPT_VALUES WHERE TYPE = 'P' AND DATEDIFF(MI,DATEADD(DD,NUMBER, #{beginDate}),#{endDate})>=0 ) YMD, ( SELECT DISTINCT a.ID AS uId, a.Name AS name, c.Account_Name AS accountName, a.JobNum AS jobNum, b.Name AS departName , b.ID AS departId, a.Account AS accountId, d.Position_Name AS positionName,a.entryDate,a.ResignDate FROM Account_Data c LEFT JOIN T_Employee_Basic a ON a.Account = c.Account_ID LEFT JOIN T_Department b ON a.Department_ID = b.ID LEFT JOIN Position_Data d ON c.Position_ID = d.Position_ID
AND a.Name=#{name}
AND b.Name=#{departName}
AND (b.ID=#{departId} OR b.ID IN ( SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId} ) OR b.ID IN ( SELECT ID FROM T_Department TD WHERE TD.ParentID IN( SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId} ) ) )
AND c.Account_Name=#{accountName}
AND d.Position_Name=#{positionName}
AND a.JobNum=#{jobNum}
AND (a.ResignDate>#{beginDate} OR a.ResignDate IS NULL or a.ResignDate='1900-01-01 00:00:00.000')
AND a.entryDate<#{endDate}
AND a.JobNum != '0' AND a.IsDelete='0'
) TDP ) YMD_TDP LEFT JOIN ( SELECT e.UserID AS accountId, f.Name AS classTimeName, f.Type AS classTimeType, f.ShiftDate AS shiftDate, f.StartTime AS startTime , f.EndTime AS endTime FROM T_Att_PeopleShift e LEFT JOIN T_Att_Shift f ON e.ShiftID = f.ID
AND f.ShiftDate>=#{beginDate}
AND f.ShiftDate <= #{endDate}
) TAPC ON YMD_TDP.accountId = TAPC.accountId AND YMD_TDP.YYYYMMDD = CONVERT(varchar(100), TAPC.shiftDate, 23) LEFT JOIN ( SELECT a.AttTime AS attTime, a.Userid AS accountId FROM T_Att_Attendance a INNER JOIN T_AttLog b ON a.AttLogID = b.ID ) TAA ON TAA.accountId = YMD_TDP.accountId AND YMD_TDP.YYYYMMDD = CONVERT(varchar(100), TAA.attTime, 23) GROUP BY YMD_TDP.YYYYMMDD, YMD_TDP.uId, YMD_TDP.name, YMD_TDP.accountName, YMD_TDP.jobNum, YMD_TDP.departName, YMD_TDP.departId,YMD_TDP.entryDate,YMD_TDP.ResignDate , YMD_TDP.accountId, YMD_TDP.positionName, TAPC.classTimeName, TAPC.classTimeType, TAPC.shiftDate, TAPC.startTime, TAPC.endTime ) YTT ) YTTD GROUP BY YTTD.uId, YTTD.name, YTTD.jobNum, YTTD.departName, YTTD.departId, YTTD.positionName,YTTD.entryDate,YTTD.ResignDate

SELECT
DISTINCT
a.ID AS uId,
a.Name AS name,
a.JobNum AS jobNum,
c.Account_Name AS accountName,
b.Name AS departName,
b.ID AS departId,
d.Position_Name AS positionName
FROM
Account_Data c
LEFT JOIN T_Employee_Basic a ON a.Account=c.Account_ID
LEFT JOIN T_Department b ON a.Department_ID=b.ID
LEFT JOIN Position_Data d ON c.Position_ID=d.Position_ID
LEFT JOIN Account_Project_Data e ON c.Account_ID=e.Account_ID
LEFT JOIN Project_Data f ON f.Project_ID=e.Project_ID


        AND a.Name=#{name}


        AND b.Name=#{departName}


        AND (b.ID=#{departId}
        OR b.ID IN (
        SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId}
        )
        OR b.ID IN (
        SELECT ID FROM T_Department TD WHERE TD.ParentID
        IN(
        SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId}
        )
        )
        )


        AND d.Position_Name=#{positionName}


        AND a.JobNum=#{jobNum}


        AND c.Account_Name=#{accountName}


        AND f.Project_ID=#{projectId}


        AND (a.ResignDate>#{beginDate} OR a.ResignDate IS NULL or a.ResignDate='1900-01-01 00:00:00.000')


        AND a.entryDate<#{endDate}

    AND a.JobNum != '0' AND a.IsDelete='0'

SELECT
DISTINCT
a.ID AS uId,
a.Name AS name,
c.Account_Name AS
accountName,
a.JobNum AS jobNum,
b.Name AS departName,
b.ID AS departId,
a.Account AS accountId,
d.Position_Name AS positionName,
f.Name AS
classTimeName,
f.Type AS classTimeType,
f.ShiftDate AS shiftDate,
f.StartTime AS startTime,
f.EndTime As endTime

FROM
Account_Data c
LEFT JOIN T_Employee_Basic a ON a.Account=c.Account_ID
LEFT JOIN T_Department b ON a.Department_ID=b.ID
LEFT JOIN Position_Data d ON
c.Position_ID=d.Position_ID
LEFT JOIN T_Att_PeopleShift e ON e.UserID
= a.Account
LEFT JOIN T_Att_Shift f ON e.ShiftID=f.ID


        AND a.Name=#{name}


        AND b.Name=#{departName}


        AND (b.ID=#{departId}
        OR b.ID IN (
        SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId}
        )
        OR b.ID IN (
        SELECT ID FROM T_Department TD WHERE TD.ParentID
        IN(
        SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId}
        )
        )
        )


        AND c.Account_Name=#{accountName}


        AND d.Position_Name=#{positionName}


        AND a.JobNum=#{jobNum}


        AND (a.ResignDate>#{beginDate} OR a.ResignDate IS NULL or a.ResignDate='1900-01-01 00:00:00.000')


        AND a.entryDate<#{endDate}


        AND f.ShiftDate>=#{beginDate}


        AND f.ShiftDate<=#{endDate}

    AND a.JobNum != '0' AND a.IsDelete='0'

SELECT DISTINCT a.UserID AS accountId, b.ShiftDate AS ctshiftDate, b.StartTime AS ctbeginDate, b.EndTime As ctEndDate FROM T_Att_PeopleShift a INNER JOIN T_Att_Shift b ON a.ShiftID=b.ID
AND b.ShiftDate=#{ctEndDate} OR b.ShiftDate BETWEEN #{ctbeginDate} AND #{ctEndDate}
AND a.UserID=#{accountId}

SELECT DISTINCT tb3.YYYYMMDD,tb3.uId,tb3.name,tb3.accountName,tb3.jobNum,tb3.entryDate,tb3.resignDate, tb3.departName,tb3.departId,tb3.accountId,tb3.positionName, tb4.Name AS classTimeName, tb4.Type AS classTimeType, tb4.ShiftDate AS shiftDate, tb4.StartTime AS startTime, tb4.EndTime As endTime FROM (SELECT tb1.*,tb2.* FROM ( SELECT CONVERT(CHAR(10), DATEADD(DD,NUMBER,#{beginDate}),120) AS YYYYMMDD FROM MASTER..SPT_VALUES WHERE TYPE = 'P' AND DATEDIFF(MI,DATEADD(DD,NUMBER, #{beginDate}),#{endDate}) >=0 )tb1 , ( SELECT DISTINCT a.ID AS uId, a.Name AS name, c.Account_Name AS accountName, a.JobNum AS jobNum, a.EntryDate AS entryDate, a.ResignDate AS resignDate, b.Name AS departName, b.ID AS departId, a.Account AS accountId, d.Position_Name AS positionName
FROM Account_Data c LEFT JOIN T_Employee_Basic a ON a.Account=c.Account_ID LEFT JOIN T_Department b ON a.Department_ID=b.ID left JOIN Position_Data d ON c.Position_ID=d.Position_ID LEFT JOIN dbo.Account_Project_Data e ON c.Account_ID=e.Account_ID LEFT JOIN dbo.Project_Data f ON f.Project_ID=e.Project_ID left JOIN T_Att_PeopleShift g ON g.UserID = a.Account
AND a.Name=#{name}
AND b.Name=#{departName}
AND (b.ID=#{departId} OR b.ID IN ( SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId} ) OR b.ID IN ( SELECT ID FROM T_Department TD WHERE TD.ParentID IN( SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId} ) ) )
AND d.Position_Name=#{positionName}
AND a.JobNum=#{jobNum}
AND (a.ResignDate>#{beginDate} OR a.ResignDate IS NULL or a.ResignDate='1900-01-01 00:00:00.000')
AND a.entryDate<#{endDate}
AND f.Project_ID=#{projectId}
)tb2 ) tb3 LEFT JOIN (SELECT i.UserID,j.EndTime,j.ShiftDate,j.StartTime,j.Name,j.Type FROM T_Att_PeopleShift i left JOIN T_Att_Shift j ON i.ShiftID=j.ID
AND j.ShiftDate >= #{beginDate} AND j.ShiftDate <= #{endDate}
) tb4 ON tb3.accountId = tb4.UserID where tb4.ShiftDate is null or CONVERT(VARCHAR(100), tb4.ShiftDate, 23) = tb3.YYYYMMDD

SELECT
max(a.AttTime) AS maxAttTime,
min(a.AttTime) AS minAttTime
FROM
T_Att_Attendance a
INNER JOIN T_AttLog b ON a.AttLogID=b.ID


        AND b.Year=#{year}


        AND b.Month=#{month}


        AND b.Day=#{day}


        AND a.Userid=#{accountId}

