    )

SELECT * FROM ( ( SELECT CONVERT(CHAR(10), DATEADD(DD,NUMBER,#{beginDate}),120) AS YYYYMMDD FROM MASTER..SPT_VALUES WHERE TYPE = 'P' AND DATEDIFF(MI,DATEADD(DD,NUMBER, #{beginDate}),#{endDate}) >=0 )tab1, ( SELECT DISTINCT a.ID AS uId, a.Name AS name, c.Account_Name AS accountName, a.JobNum AS jobNum, b.Name AS departName, b.ID AS departId, a.Account AS accountId, d.Position_Name AS positionName, a.EntryDate AS entryDate, a.ResignDate AS resignDate
FROM Account_Data c LEFT JOIN T_Employee_Basic a ON a.Account=c.Account_ID LEFT JOIN T_Department b ON a.Department_ID=b.ID left JOIN Position_Data d ON c.Position_ID=d.Position_ID LEFT JOIN dbo.Account_Project_Data e ON c.Account_ID=e.Account_ID LEFT JOIN dbo.Project_Data f ON f.Project_ID=e.Project_ID left JOIN T_Att_PeopleShift g ON g.UserID = a.Account
AND a.Name=#{name}
AND b.Name=#{departName}
AND (b.ID=#{departId} OR b.ID IN ( SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId} ) OR b.ID IN ( SELECT ID FROM T_Department TD WHERE TD.ParentID IN( SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId} ) ) )
AND a.JobNum=#{jobNum}
AND (a.ResignDate>#{beginDate} OR a.ResignDate IS NULL or a.ResignDate='1900-01-01 00:00:00.000')
AND a.entryDate<#{endDate}
AND a.JobNum is not null AND a.JobNum != '0' AND a.Account is not null AND a.Department_ID is not null AND c.Position_ID IS NOT NULL AND a.State='1'
)tab2
AND tab2.EntryDate >= #{endDate} AND tab2.ResignDate <= #{beginDate}
)tab3
LEFT JOIN
( SELECT i.UserID,j.EndTime AS endDate,j.ShiftDate AS shiftDate,j.StartTime AS beginDate,j.Name,j.Type AS classTimeType FROM T_Att_PeopleShift i left JOIN T_Att_Shift j ON i.ShiftID=j.ID
AND j.ShiftDate >= #{beginDate} AND j.ShiftDate <= #{endDate}
)tab4 ON tab3.accountId = tab4.UserID ) where CONVERT(VARCHAR(100), tab4.ShiftDate, 23) = tab3.YYYYMMDD or tab4.ShiftDate is null order by tab3.departId

SELECT DISTINCT a.ID AS uId, b.Name AS departName, a.JobNum AS jobNum, b.ID AS departId, d.Position_Name AS positionName, f.Name AS classTimeName, f.Type AS classTimeType, f.ShiftDate AS shiftDate, f.StartTime AS startTime, f.EndTime As endTime
FROM Account_Data c LEFT JOIN T_Employee_Basic a ON a.Account=c.Account_ID LEFT JOIN T_Department b ON a.Department_ID=b.ID INNER JOIN Position_Data d ON c.Position_ID=d.Position_ID INNER JOIN T_Att_PeopleShift e ON e.UserID = a.ID INNER JOIN T_Att_Shift f ON e.ShiftID=f.ID
AND b.Name=#{departName}
AND (b.ID=#{departId} OR b.ID IN ( SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId} ) OR b.ID IN ( SELECT ID FROM T_Department TD WHERE TD.ParentID IN( SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId} ) ) )
AND f.ShiftDate>=#{beginDate}
AND f.ShiftDate<=#{endDate}
AND a.JobNum != '0'

SELECT
DISTINCT
a.ID AS uId,
a.Name AS name,
a.JobNum AS jobNum,
c.Account_Name AS accountName,
b.Name AS departName,
b.ID AS departId,
d.Position_Name AS positionName
FROM
Account_Data c
LEFT JOIN T_Employee_Basic a ON a.Account=c.Account_ID
LEFT JOIN T_Department b ON a.Department_ID=b.ID
LEFT JOIN Position_Data d ON
c.Position_ID=d.Position_ID


        AND a.Name=#{name}


        AND b.Name=#{departName}


        AND (b.ID=#{departId}
        OR b.ID IN (
        SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId}
        )
        OR b.ID IN (
        SELECT ID FROM T_Department TD WHERE TD.ParentID
        IN(
        SELECT ID FROM T_Department TD WHERE TD.ParentID=#{departId}
        )
        )
        )


        AND d.Position_Name=#{positionName}


        AND a.JobNum=#{jobNum}


        AND (a.ResignDate>#{beginDate} OR a.ResignDate IS NULL or a.ResignDate='1900-01-01 00:00:00.000')


        AND a.entryDate<#{endDate}

    AND a.JobNum != '0' AND a.State = '1'

order by b.Name
