mapper.select:
SELECT a.Account_ID AS accountId, a.Account_Name AS accountName, a.Account_Pwd AS accountPwd, a.Account_State AS accountState, a.Position_ID AS positionId, a.Account_Mac AS accountMac FROM dbo.Account_Data a
AND a.Account_ID=#{accountId}
AND a.Account_Name=#{accountName}

mapper.update:
UPDATE
dbo.Resign_Reason
SET
dbo.Resign_Reason.Reason_Name =#{reasonName}
WHERE
dbo.Resign_Reason.Reason_ID = #{reasonId}

mapper.select:
SELECT DISTINCT a.ID AS id, a.Account AS uAccountId, a.JobNum AS JobNum, a.Name AS name, a.Sex AS sex, d.Name AS departName, d.ID AS departId, c.Position_Name AS positionName, b.Account_Name AS ERPAaccount, b.Account_ID AS accountId, a.Birth AS birth, a.EntryDate AS entryDate, a.PositiveDate AS PositiveDate, CASE WHEN a.ResignDate<a.EntryDate THEN NULL ELSE a.ResignDate END AS ResignDate, a.State AS State, a.ResignType AS resignType, g.Reason_Id AS resignReasonId, g.Reason_Name AS resignName FROM dbo.Account_Data b LEFT JOIN dbo.T_Employee_Basic a ON a.Account=b.Account_ID LEFT JOIN dbo.Position_Data c ON b.Position_ID=c.Position_ID LEFT JOIN dbo.T_Department d ON a.Department_ID=d.ID LEFT JOIN dbo.Account_Project_Data e ON b.Account_ID=e.Account_ID LEFT JOIN dbo.Project_Data f ON e.Project_ID=f.Project_ID LEFT JOIN Resign_Reason g ON a.ResignReason = g.Reason_ID
AND d.Name=#{departName}
AND a.State=#{state}
AND f.Project_ID=#{projectId}
AND (a.JobNum like '%'+#{searchContent}+'%' OR a.name like '%'+#{searchContent}+'%' OR d.Name like '%'+#{searchContent}+'%' OR c.Position_Name like '%'+#{searchContent}+'%')
AND a.EntryDate >= #{enterStartDate} AND a.EntryDate <=#{enterEndDate}
AND a.ResignDate >= #{leaveStartDate} AND a.ResignDate <=#{leaveEndDate}
AND a.PositiveDate >= #{zzStartDate} AND a.PositiveDate <=#{zzEndDate}
AND a.ResignType = #{resignType}
AND a.ResignDate = #{resignReasonId}
AND Month(a.Birth)=#{birth}
AND a.isDelete='0'
ORDER by b.Account_ID

SELECT ID AS ID, appellation AS appellation, name AS chname, workaddress AS workAddress, position AS position, phone AS phone, wechat AS wechat, address AS famAddress, Birthday AS birth
FROM dbo.Account_Family
where Account_ID=#{uAccountId}

SELECT a.Name AS name, a.ID AS id, a.JobNum AS jobNum, a.Sex AS sex, a.Birth AS birth, a.Account AS accountId, a.ID AS positionId, a.EntryDate AS entryDate, a.PositiveDate AS positiveDate, a.ResignDate AS resignDate, a.ResignType AS resignType, a.ResignReason AS resignReason, a.State AS state, a.Remark AS remark FROM dbo.T_Employee_Basic a
AND a.Name=#{name}
AND a.JobNum=#{jobNum}
AND a.ID=#{id}

mapper.update:
update
T_Employee_Basic
set
PositiveDate=#{positiveDate}
where
ID=#{id}

update
T_Employee_Basic
set
ResignDate=#{resignDate}
where
ID=#{id}

update
T_Employee_Basic
set
IsDelete=#{isDelete}
where
ID=#{id}
