mapper.select:
SELECT COMPUTER.ID, T1.NAME AS DEPARTMENTNAME, COMPUTER.ID AS CID, TYPE_ID, DEPARTMENTID , START_TIME,JOB_ID, POSITION_NAME, ARTICLES_NAME, A5.ACCOUNT_NAME AS USERNAME, A1.ACCOUNT_NAME AS APPLYNAME , A2.ACCOUNT_NAME AS ZHUNAME, A3.ACCOUNT_NAME AS FUNAME, END_TIME , CASE WHEN PREPARE_NAME IS NULL THEN '未交接' WHEN PREPARE_NAME IS NOT NULL THEN PREPARE_NAME END AS PREPARE_NAME , CASE WHEN IS_RESERVE = 2 THEN '预申请' WHEN IS_RESERVE = 0 THEN '普通' END AS IS_RESERVE , CASE WHEN ZHU_ADVICE = 0 THEN '未审核' WHEN ZHU_ADVICE = 1 THEN '同意' WHEN ZHU_ADVICE = 2 THEN '不同意' END AS ZHU_ADVICE , CASE WHEN IS_ING IS NULL THEN '点击进行' WHEN IS_ING = 1 THEN '进行中' WHEN IS_ING = 2 THEN '已完成' END AS IS_ING , CASE WHEN FU_ADVICE = 0 THEN '未审核' WHEN FU_ADVICE = 1 THEN '同意' WHEN FU_ADVICE = 2 THEN '不同意' END AS FU_ADVICE FROM COMPUTER LEFT JOIN ACCOUNT_DATA A1 ON A1.ACCOUNT_ID = COMPUTER.APPLY_ID LEFT JOIN ACCOUNT_DATA A2 ON A2.ACCOUNT_ID = COMPUTER.ZHU_ID LEFT JOIN ACCOUNT_DATA A3 ON A3.ACCOUNT_ID = COMPUTER.FU_ID LEFT JOIN ACCOUNT_DATA A4 ON A4.ACCOUNT_ID = COMPUTER.PREPARE_ID LEFT JOIN ACCOUNT_DATA A5 ON A5.ACCOUNT_ID = COMPUTER.USER_ID LEFT JOIN T_DEPARTMENT T1 ON DEPARTMENTID = T1.ID LEFT JOIN POSITION_DATA ON POSITION_DATA.POSITION_ID = JOB_ID WHERE COMPUTER.ID >= 0 AND (IS_RESERVE = 0 OR IS_RESERVE = 2)
AND START_TIME >= #{begDate} AND START_TIME < #{endDate}
AND END_TIME >= #{begDate} AND END_TIME < #{endDate}
AND A1.ACCOUNT_NAME LIKE '%' + #{applyName} + '%'
AND (USER_ID = #{accountId} OR FU_ID = #{accountId} OR USER_NAME = #{accountName} OR APPLY_ID = #{accountId} OR ZHU_ID = #{accountId} OR PEI_JIEDAN_ID = #{accountId} OR PREPARE_NAME = #{accountName})
AND DEPARTMENTID = #{DepartmentID}
AND FU_ADVICE = 1 AND (ZHU_ADVICE = 0 OR FU_ADVICE = 0) AND (ZHU_ADVICE = 2 OR FU_ADVICE = 2)
AND TYPE_ID = #{typeId}
AND PREPARE_NAME = #{prepareName}
AND A5.ACCOUNT_NAME LIKE '%' + #{userName} + '%'
AND IS_ING IS NULL AND IS_ING = #{isIng}
AND IS_RESERVE = #{isReserve}
