mapper.select:
SELECT G.ID AS ID, G.GO_OUT_APPLY_TIME , G.GO_OUT_DEPARTMENT_ID, T.NAME AS GOOUTDEPARTMENTNAME, GO_OUT_NAME, G.GO_OUT_APPLY_ID , A2.ACCOUNT_NAME AS GOOUTAPPLYNAME , G.GO_OUT_ADDRESS , G.GO_OUT_REGION , G.GO_OUT_GO_TIME, G.GO_OUT_TONGZHI_ID , A3.ACCOUNT_NAME AS GOOUTTONGZHINAME, GO_OUT_TONGZHI_ADVICE , GO_OUT_COME_TIME , CASE WHEN GO_OUT_OTHERTYPE = 1 THEN '班中外出' WHEN GO_OUT_OTHERTYPE = 2 THEN '班前外出' END AS GO_OUT_OTHERTYPE , CASE WHEN GO_OUT_TYPE = 1 THEN '是' WHEN GO_OUT_TYPE = 2 THEN '否' END AS GO_OUT_TYPE , CASE WHEN GO_OUT_DELETE IS NULL THEN '点击取消' WHEN GO_OUT_DELETE = 1 THEN '已取消' END AS GO_OUT_DELETE FROM GO_OUT_REGISTER G LEFT JOIN ACCOUNT_DATA A2 ON A2.ACCOUNT_ID = G.GO_OUT_APPLY_ID LEFT JOIN T_DEPARTMENT T ON T.ID = G.GO_OUT_DEPARTMENT_ID LEFT JOIN ACCOUNT_DATA A3 ON A3.ACCOUNT_ID = G.GO_OUT_TONGZHI_ID
AND GO_OUT_DEPARTMENT_ID = #{goOutDepartmentId}
AND GO_OUT_NAME LIKE #{goOutName} + '%'
AND G.GO_OUT_APPLY_TIME >= #{begDate} AND G.GO_OUT_APPLY_TIME < #{endDate}
AND G.GO_OUT_COME_TIME >= #{begDate} AND G.GO_OUT_COME_TIME < #{endDate}
AND G.GO_OUT_GO_TIME >= #{begDate} AND G.GO_OUT_GO_TIME < #{endDate}
AND G.GO_OUT_DELETE = 1
AND G.GO_OUT_DELETE IS NULL
AND G.ID = #{id}

mapper.update:
UPDATE GO_OUT_REGISTER
SET
     GO_OUT_COME_TIME = #{goOutComeTime}
    , GO_OUT_GO_TIME = #{goOutGoTime}
    , GO_OUT_TYPE = #{goOutType}
    , GO_OUT_ADDRESS = #{goOutAddress}
    , GO_OUT_REGION = #{goOutRegion}
    , GO_OUT_TONGZHI_ID = #{goOutTongzhiId}
    , GO_OUT_TONGZHI_ADVICE = #{goOutTongzhiAdvice}
    , GO_OUT_OTHERTYPE = #{goOutOthertype}
WHERE ID = #{id} AND  GO_OUT_DELETE IS NULL AND GO_OUT_GO_TIME > GETDATE()

mapper.select:
SELECT
    EMP_NAME, GENDER, JOBNUM, DEPARTMENT_ID, BIRTH, ENTRYDATE, POSITIVEDATE, RESIGNDATE,
    UPDATEDATE, CREATEDATE, ISDELETE, STATE, CREATEUSER, ACCOUNT, ACCOUNT_ID,
    ACCOUNT_NAME, REMARK, ID, POSITION_ID, ACCOUNT_STATE
FROM V_ACCOUNTDATA


        AND DEPARTMENT_ID = #{params.deptId}


        AND STATE = #{params.state}


       AND POSITION_ID = #{params.positionId}


       AND ACCOUNT = #{params.account}


       AND ACCOUNT_ID = #{params.accountId}
