SELECT OFFICESUPPLIESDATA.ID AS ID, T_DEPARTMENT.NAME DEPARTMENTNAME,DEPARTMENTID, CONVERT(VARCHAR(7), MONTH, 120) AS MONTH, OFFICESUPPLIESDATA.NAME,NUM,SPECIFICATION, REMARK,ACCOUNTID_2,ACCOUNTID_1, CASE WHEN ADVICE_2 = 3 THEN '未审核' WHEN ADVICE_2 = 1 THEN '同意' WHEN ADVICE_2 = 2 THEN '不同意' END AS ADVICE_2, CASE WHEN STATE = 0 THEN '等待' WHEN STATE = 1 THEN '通过'WHEN STATE = 2 THEN '没通过' END AS STATE, A1.ACCOUNT_NAME AS ACCOUNT1NAME,A2.ACCOUNT_NAME AS ACCOUNT2NAME,D.OFFICEID,D.SPECID FROM OFFICESUPPLIESDATA LEFT JOIN T_DEPARTMENT ON T_DEPARTMENT.ID = OFFICESUPPLIESDATA.DEPARTMENTID LEFT JOIN ACCOUNT_DATA A1 ON A1.ACCOUNT_ID = ACCOUNTID_1 LEFT JOIN ACCOUNT_DATA A2 ON A2.ACCOUNT_ID = ACCOUNTID_2 LEFT JOIN ( SELECT A.ID OFFICEID,A.NAME OFFICENAME,B.ID SPECID,B.SPECIFICATION SPECNAME FROM OFFICE_NAME A,SPECIFICATION B WHERE A.ID = B.NAME_ID ) D ON D.OFFICENAME = OFFICESUPPLIESDATA.NAME AND D.SPECNAME = OFFICESUPPLIESDATA.SPECIFICATION WHERE YEAR(MONTH) * 100 + MONTH(MONTH) = #{month}
AND (ACCOUNTID_1 = #{accountId1} OR ACCOUNTID_2 = #{accountId1})
AND DEPARTMENTID = #{departmentId}
AND OFFICESUPPLIESDATA.NAME LIKE '%' + #{name} + '%'
AND STATE = #{state}
AND ADVICE_2 = #{advice2}
ORDER BY NUM

