LEFT JOIN POSITION_TYPE
ON POSITION_DATA.POSITIONTYPE_ID = POSITION_TYPE.POSITIONTYPE_ID

    AND STATE = '1' AND ISDELETE = '0'


            AND
            (
            POSITION_TYPE.POSITIONTYPE_ID = 5
