            OR POSITION_NAME = '副总经理'
            )


            AND
            (
            POSITION_TYPE.POSITIONTYPE_ID = 5
            OR POSITION_NAME = '项目经理'
            )






                    AND (DEPARTMENT_ID = 39)


                    AND (
                    DEPARTMENT_ID IN (
