SELECT OU.ID, OU.ACCOUNTID, ACCOUNT_NAME, O.NAME, OU.NUM , OU.USER_NAME,OFFICEID , CONVERT(VARCHAR(16), STARTTIME, 120) AS STARTTIME , CONVERT(VARCHAR(16), ENDTIME, 120) AS ENDTIME , ACCOUNTZT, REMARK, YUYUE_TIME , CASE WHEN STATE = 1 THEN '已完成' WHEN STATE = 0 THEN '未完成' WHEN STATE = 2 THEN '未完成' WHEN STATE = 3 THEN '已取消' END AS STATE , CASE WHEN TYPE = 2 THEN '例会' WHEN TYPE = 1 THEN '——' END AS CONFERENCE,TYPE FROM OFFICEUSERDATA OU LEFT JOIN ACCOUNT_DATA A ON A.ACCOUNT_ID = OU.ACCOUNTID LEFT JOIN OFFICEDATA O ON OU.OFFICEID = O.ID
AND STARTTIME >= #{startTime} AND ENDTIME <= #{endTime}
AND O.ID = #{officeId}
AND STATE = #{state}
AND O.ID IN
#{item}
AND STATE IN
#{item}
ORDER BY STARTTIME

