mapper.select:
SELECT DISTINCT LQ.ID AS id ,LQ.LeaveType AS leaveType ,LQ.AddTime AS addTime ,LQ.LeaveAccount AS leaveAccount ,AD.Account_Name AS leaveAccountName ,TEB.Department_ID AS departId ,TD.Name AS departName ,PD.Position_ID AS positionId ,PD.Position_Name AS positionName ,LQ.AgentAccount AS agentAccount ,ADA.Account_Name AS agentAccountName ,LQ.StartTime AS startTime ,LQ.EndTime AS endTime ,LQ.Leave_Remark AS leaveRemark ,LQ.Account1 AS account1 ,AD1.Account_Name AS accountName1 ,LQ.Time1 AS time1 ,CASE WHEN LQ.Result1 = 1 THEN '同意' WHEN LQ.Result1 = 0 THEN '不同意' ELSE NULL END AS result1 ,LQ.Remark1 AS remark1 ,LQ.Account2 AS account2 ,AD2.Account_Name AS accountName2 ,LQ.Time2 AS time2 ,CASE WHEN LQ.Result2 = 1 THEN '同意' WHEN LQ.Result2 = 0 THEN '不同意' ELSE NULL END AS result2 ,LQ.Remark2 AS remark2 ,LQ.Account3 AS account3 ,AD3.Account_Name AS accountName3 ,LQ.Time3 AS time3 ,CASE WHEN LQ.Result3 = 1 THEN '同意' WHEN LQ.Result3 = 0 THEN '不同意' ELSE NULL END AS result3 ,LQ.Remark3 AS remark3 ,LQ.Account4 AS account4 ,AD4.Account_Name AS accountName4 ,LQ.Time4 AS time4 ,CASE WHEN LQ.Result4 = 1 THEN '同意' WHEN LQ.Result4 = 0 THEN '不同意' ELSE NULL END AS result4 ,LQ.Remark4 AS remark4 ,LQ.update_Time AS updateTime ,LQ.isSee AS isSee ,LQ.step AS step ,LQ.BaobeiID AS baobeiID FROM Leave_Qingjia LQ LEFT JOIN Account_Data AD ON LQ.LeaveAccount=AD.Account_ID LEFT JOIN Account_Data ADA ON LQ.AgentAccount=ADA.Account_ID LEFT JOIN T_Employee_Basic TEB ON AD.Account_ID=TEB.Account LEFT JOIN Position_Data PD ON PD.Position_ID=AD.Position_ID LEFT JOIN T_Department TD ON TD.ID=TEB.Department_ID LEFT JOIN Account_Data AD1 ON LQ.Account1=AD1.Account_ID LEFT JOIN Account_Data AD2 ON LQ.Account2=AD2.Account_ID LEFT JOIN Account_Data AD3 ON LQ.Account3=AD3.Account_ID LEFT JOIN Account_Data AD4 ON LQ.Account4=AD4.Account_ID
AND LQ.AddTime >=#{beginDate}
AND LQ.AddTime <=#{endDate}
AND (LQ.LeaveAccount=#{accountId} OR LQ.AgentAccount =#{accountId} OR LQ.Account1 =#{accountId} OR LQ.Account2 =#{accountId} OR LQ.Account3 =#{accountId} OR LQ.Account4 =#{accountId})
AND TEB.Department_ID =#{departId}
AND LQ.LeaveAccount =#{name}
AND LQ.Result1 IS NULL AND LQ.Result2 IS NULL AND LQ.Result3 IS NULL AND LQ.Result4 IS NULL
AND LQ.Result4 IS NULL AND LQ.Result1 IS NOT NULL AND LQ.Result1 != 0 AND (LQ.Result2 IS NULL OR LQ.Result2 != 0) AND (LQ.Result3 IS NULL OR LQ.Result3 != 0)
AND LQ.Time4 IS NOT NULL AND LQ.Result4 = 1
AND (LQ.Result1 = 0 OR LQ.Result2 = 0 OR LQ.Result3 = 0 OR LQ.Result4 = 0)

SELECT TD.ID AS departId ,TD.Name AS departName ,AD.Account_ID AS employeeId ,AD.Account_Name AS employeeName ,PD.Position_Name AS positionName ,PD.Position_ID AS positionId from T_Department TD LEFT JOIN T_Employee_Basic TEB ON TD.ID = TEB.Department_ID LEFT JOIN Account_Data AD ON AD.Account_ID= TEB.Account LEFT JOIN Position_Data PD ON PD.Position_ID=AD.Position_ID
AND ( EXISTS (SELECT TDC.ID,TDC.Name,TDC.OrderNum,TDC.ParentID FROM T_Department TDC WHERE TDC.ParentID=#{departId} AND TD.ParentID= TDC.ID) OR TD.ParentID=#{departId} OR TD.ID=#{departId} )
AND AD.Account_Name =#{name}
AND TEB.IsDelete = '0' AND TEB.State='1' AND AD.Account_ID != #{accountId}
ORDER BY AD.Account_Name

mapper.update:
UPDATE Leave_Qingjia SET
LeaveType=#{leaveType} , AgentAccount=#{agentAccount}, StartTime=#{startTime}, EndTime=#{endTime} , Leave_Remark=#{leaveRemark} ,
Account1=#{account1}, Time1=#{time1}, Result1=#{result1}, Remark1=#{remark1},
Account2=#{account2}, Time2=#{time2}, Result2=#{result2}, Remark2=#{remark2},
Account3=#{account3}, Time3=#{time3}, Result3=#{result3}, Remark3=#{remark3},
Time4=#{time4}, Result4=#{result4}, Remark4=#{remark4}, Account4=#{account4}
AND LeaveAccount=#{leaveAccount}
AND ID=#{id}

mapper.select:
SELECT LD.ID AS id ,LD.Reason AS reason ,LD.LeaveAccount AS leaveAccount ,AD.Account_Name AS leaveAccountName ,TD.Name AS departName ,LD.AddTime AS addTime ,LD.StartTime AS startTime ,LD.EndTime AS endTime ,LD.LeaveTotal AS leaveTotal ,LD.WorkTime AS workTime ,LD.WorkTotal AS workTotal ,LD.Leave_Remark AS leaveRemark ,LD.Account1 AS account1 ,AD1.Account_Name AS accountName1 ,LD.Time1 AS time1 ,CASE WHEN LD.Result1 = 1 THEN '同意' WHEN LD.Result1 = 0 THEN '不同意' ELSE NULL END AS result1 ,LD.Remark1 AS remark1 ,LD.Account2 AS account2 ,AD2.Account_Name AS accountName2 ,LD.Time2 AS time2 ,CASE WHEN LD.Result2 = 1 THEN '同意' WHEN LD.Result2 = 0 THEN '不同意' ELSE NULL END AS result2 ,LD.Remark2 AS remark2 ,LD.Account3 AS account3 ,AD3.Account_Name AS accountName3 ,LD.Time3 AS time3 ,CASE WHEN LD.Result3 = 1 THEN '同意' WHEN LD.Result3 = 0 THEN '不同意' ELSE NULL END AS result3 ,LD.Remark3 AS remark3 ,LD.Account4 AS account4 ,AD4.Account_Name AS accountName4 ,LD.Time4 AS time4 ,CASE WHEN LD.Result4 = 1 THEN '同意' WHEN LD.Result4 = 0 THEN '不同意' ELSE NULL END AS result4 ,LD.Remark4 AS remark4 ,LD.update_Time AS updateTime ,LD.isSee AS isSee ,LD.step AS step
FROM Leave_Daoxiu LD LEFT JOIN Account_Data AD ON LD.LeaveAccount=AD.Account_ID LEFT JOIN T_Employee_Basic TEB ON AD.Account_ID=TEB.Account LEFT JOIN Position_Data PD ON PD.Position_ID=AD.Position_ID LEFT JOIN T_Department TD ON TD.ID=TEB.Department_ID LEFT JOIN Account_Data AD1 ON LD.Account1=AD1.Account_ID LEFT JOIN Account_Data AD2 ON LD.Account2=AD2.Account_ID LEFT JOIN Account_Data AD3 ON LD.Account3=AD3.Account_ID LEFT JOIN Account_Data AD4 ON LD.Account4=AD4.Account_ID
AND LD.AddTime >=#{beginDate}
AND LD.AddTime <=#{endDate}
AND (LD.LeaveAccount=#{accountId} OR LD.Account1 =#{accountId} OR LD.Account2 =#{accountId} OR LD.Account3 =#{accountId} OR LD.Account4 =#{accountId})
AND TEB.Reason =#{restReason}
AND TEB.Department_ID =#{departId}
AND AD.Account_Name =#{name}
AND LD.Result1 IS NULL AND LD.Result2 IS NULL AND LQ.Result3 IS NULL AND LD.Result4 IS NULL
AND LD.Result4 IS NULL AND LD.Result1 IS NOT NULL AND LQ.Result1 != 0 AND (LD.Result2 IS NULL OR LD.Result2 != 0) AND (LD.Result3 IS NULL OR LD.Result3 != 0)
AND LD.Time4 IS NOT NULL AND LD.Result4 = 1
AND (LD.Result1 = 0 OR LD.Result2 = 0 OR LD.Result3 = 0 OR LD.Result4 = 0)

SELECT P.ID AS id ,P.Department_ID AS departmentId ,P.Position_ID AS positionId ,P.Apply_Time AS applyTime ,P.Need_Time AS needTime ,P.Apply_Reason AS applyReason ,P.Num AS num ,P.Shencha_ID AS shenchaId ,P.Shencha_Time AS shenchaTime ,P.Shencha_Advice AS shenchaAdvice ,P.Shencha_See AS shenchaSee ,P.Renli_ID AS renliId ,P.Renli_Time AS renliTime ,P.Renli_Advice AS renliAdvice ,P.Renli_See AS renliSee ,P.Shenpi_ID AS shenpiId ,P.Shenpi_Time AS shenpiTime ,P.Shenpi_Advice AS shenpiAdvice ,P.Shenpi_See AS shenpiSee ,P.Apply_ID AS applyId ,P.Is_Yes AS isYes ,P.Yes_Time AS yesTime ,P.Sex AS sex ,P.Age AS age ,P.Obsterical AS obsterical ,P.Education AS education ,P.Major AS major ,P.Industry AS industry ,P.YearsOfWorking AS yearsOfWorking ,P.Job_Happing AS jobHapping ,P.Computer_Level AS computerLevel ,P.ForeignLanguage_Level AS foreignLanguageLevel ,P.Essential_Condition AS essentialCondition ,P.Supplement AS supplement ,P.File_name AS fileName ,P.Apply_type AS applyType ,P.state AS state FROM Personnel P LEFT JOIN Account_Data AD1 ON P.Shencha_ID=AD1.Account_ID LEFT JOIN Account_Data AD2 ON P.Renli_ID=AD2.Account_ID LEFT JOIN Account_Data AD3 ON Shenpi_ID=AD3.Account_ID LEFT JOIN Account_Data AD4 ON P.Apply_ID=AD4.Account_ID
AND LD.AddTime >=#{beginDate}
AND LD.AddTime <=#{endDate}
AND (LD.Account1 =#{accountId} OR LD.Account2 =#{accountId} OR LD.Account3 =#{accountId} OR LD.Account4 =#{accountId})
AND P.Position_ID =#{positionId}
AND P.Department_ID =#{departId}

