, CERTIFICATE = #{certificate}
, CHANNEL = #{channel}
, POSITION = #{position}
, DEPARTMENT = #{department}
, WAGES = #{wages}
, INTERVIEWER = #{interviewer}
, RECRUITDATE = #{recruitDate}
, INVITATIONDATE = #{invitationDate}
, SCORE = #{score}
, ISQUALIFIED = #{isQualified}
, ISPAY = #{isPay}
, REMARK = #{remark}
, UPDATEDATE = #{updateDate}
, UPDATEACCOUNT = #{updateAccount}
WHERE ID = #{id}

UPDATE ZP_RECRUITDATA
SET ISDELETE = 1
WHERE ID = #{id}

mapper.select:
SELECT A.PLAN_ID, A.DEPARTMENT, B.NAME DEPARTMENTNAME, A.POSITION, C.POSITION_NAME POSITIONNAME, A.LACKNUM, A.PLANNUM, A.PHONENUM, A.PLANDATE, A.IS_YES, A.IS_BACK FROM ZP_PLAN AS A LEFT JOIN T_DEPARTMENT AS B ON A.DEPARTMENT = B.ID LEFT JOIN POSITION_DATA AS C ON A.POSITION = C.POSITION_ID
AND A.PLANDATE >= #{begDate} AND A.PLANDATE < #{endDate}
AND A.PLANDATE < #{endDate}
AND A.DEPARTMENT = #{department}
AND C.POSITION_ID = #{position}
AND A.IS_YES IS NULL
AND A.IS_YES = #{isYes}
AND A.IS_BACK IS NULL
AND A.IS_BACK = #{isBack}

SELECT A.PUBLISH_ID,A.CHANNEL,B.RECRUITCHANNEL_NAME CHANNELNAME,A.POSITION,
C.POSITION_NAME,A.PUBLISHNUM, A.PHONENUM, A.PUBLISHDATE
FROM ZP_PUBLISH A,ZP_RECRUITCHANNEL B,POSITION_DATA C
WHERE A.CHANNEL = B.RECRUITCHANNEL_ID AND A.POSITION = C.POSITION_ID

    AND A.PUBLISHDATE >= #{begDate}
     AND A.PUBLISHDATE < #{endDate} 


    AND A.CHANNEL = #{channel}


    AND A.POSITION = #{position}

mapper.update:
UPDATE ZP_PUBLISH
SET
CHANNEL = #{channel}
, POSITION = #{position}
, PUBLISHNUM = #{publishNum}
, PHONENUM = #{phoneNum}
, PUBLISHDATE = #{publishDate}
WHERE PUBLISH_ID = #{publishId}
